const api = require("./api");

async function geolocation({ key, address, dataType = "json", region = "br" }) {
  const addressFormated = address.split(" ").join("+");

  const { data } = await api.get(
    `/${dataType}?key=${key}&address=${addressFormated}&region=${region}`
  );
  return data.results
}

module.exports = geolocation;
